import './App.css';
import React from 'react';

function handleClick() {
  console.log('Clicou no 1 botão!')
}

function handleClick1() {
  console.log('Clicou no 2 botão!')
}

function handleClick2() {
  console.log('Clicou no 3 botão!')
}
class App extends React.Component {
  /* Repare que, diferentemente do HTML, no
  JSX você associa uma função a um evento
  passando a própria função entre chaves `{}` */
  render() {
    return (

      <div>
    <button onClick={handleClick}>Meu botão</button>
    <button onClick={handleClick1}>Meu botão</button>
    <button onClick={handleClick2}>Meu botão</button>
    </div> 
      )
  }
}

export default App;
