import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';

export class ClientesCadastrados extends Component {
  render() {
    const { clientRegistered } = this.props;
    return (
      <div>   
        <p>Nenhum cliente cadastrado</p>
        <Link to='/cadastrocliente'>Cadastrar cliente</Link>
        <ul>
          { clientRegistered.map((cliente) => <li>{cliente.clients.client.name}</li>) }
        </ul>
      </div>
    )
  }
}

const mapStateToProps = (state) => ({
  clientRegistered: [...state],
})

export default ClientesCadastrados;
